import React, { useState, useEffect } from 'react';
import '../index.css';
import $ from 'jquery';
import Header from '../components/header/Header';
import styled from 'styled-components';
import { PostButton } from '../components/buttons/Buttons';
import { ActivityCard, ChooseActivity } from '../components/card/card';
import { SearchBar } from '../components/input/Input';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import PostService from '../components/services/PostService';

const ButtonOne = styled.button`
    color: pink;
`

const Home = () => {
    // const [buttonState, setButtonState] = useState(false);
    // const [count, setCount] = useState(0);
    // const [count, setCount] = useState(0);
    const [posts, setPosts] = useState([]);
    const [postColor, setPostColor] = useState(0);

    //   useEffect(() => {
    //     setInterval(() => setDate(new Date()), 3000)
    //   })

    // const getPostInfo = async() => {
    //     const response = database.ref('posts');
    //     const data = await response.get();
    //     console.log(posts);
    //     data.docs.forEach(item => {
    //         setPosts([...posts, item.data()])
    //     })
    // }

    const onDataChange = (items) => {
        let posts = [];


        items.forEach((item) => {
            let key = item.key;
            let data = item.val();
            posts.push({
                key: key,
                name: data.name,
                description: data.description,
                about: data.about,
                category: data.category
            });

            console.log(data.category)

            if(data.category === 'Hiking') {
                setPostColor(0)
            } else if (data.category === 'Backpacking') {
                setPostColor(1)
            } else if (data.category === 'Climbing') {
                setPostColor(2)
            }
            console.log(postColor)

        });
        setPosts(posts);
    }

     useEffect(() => {
        // const info = database.ref('posts');
        // console.log(info);
        // var onDataChange = info.on('value', function(snapshot) {
        //     snapshot.forEach(function(childSnapshot) {
        //       var childKey = childSnapshot.key;
        //       var childData = childSnapshot.val();
        //       // ...
        //     });
        //   });
        // database.collection('posts').onSnapshot((snapshot) => setPosts(snapshot.docs.map((doc) => doc.data())));
        PostService.getAll().on('value', onDataChange);

        posts.forEach (() => {
            // console.log(posts)
            if(postColor === 0) {
                $('.activityCard').addClass('hiking')
            } else if (postColor === 1) {
                $('.activityCard').addClass('backpacking')
            } else if (postColor === 2) {
               $('.activityCard').addClass('climbing')
            }
            console.log(postColor)
        })

        // return () => {
        //     PostService.getAll().off('value', onDataChange);
        // }
    }, [])    


    const getUserInfo = () => {
        // grab user info from API and add to cards
    }

    return (
        <Container fluid className='text-center' id='container' style={{maxWidth: '1200px'}}>
            <Row>
                <Header />
            </Row>
  <Row></Row>
            <PostButton />
            {/* <SearchBar /> */}
            <div>
                {/* <ButtonOne onClick={ClickButton}>Browse All</ButtonOne> */}
            </div>

            <ChooseActivity name='Hiking' />
            <ChooseActivity name='Biking' />
            <ChooseActivity name='Climbing' />
            <ChooseActivity name='Trail Running' />
            <ChooseActivity name='Rafting' />
            <ChooseActivity name='Backpacking' />
            <ChooseActivity name='Ice Climbing' />
            <ChooseActivity name='Bike Brewery Tours' />
            <ChooseActivity name='Paragliding' />
            <ChooseActivity name='Other' />

           <div>
           {posts.map((post) => (
               <div style={{width: '12rem', height: '12rem', border: '1px solid black', display: 'inline-block', margin: '1rem'}} id='color' className='activityCard'>
               <h3>{post.name}</h3>
               <h6>{post.description}</h6>
               <h6>{post.about}</h6>
               </div>
           ))}
           </div>


      </Container>
    )
}

export default Home;
