import React, { useState, useEffect } from 'react';
import '../index.css';
import $ from 'jquery';
import Header from '../components/header/Header';
// import styled from 'styled-components';
import { PostButton } from '../components/buttons/Buttons';
import { ChooseActivity } from '../components/card/card';
// import { SearchBar } from '../components/input/Input';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
// import Col from 'react-bootstrap/Col';
import PostService from '../components/services/PostService';

const Home = () => {
    const [posts, setPosts] = useState([]);
    // const [postColor, setPostColor] = useState(0);

    const onDataChange = (items) => {
        let posts = [];

        items.forEach((item) => {
            let key = item.key;
            let data = item.val();
            posts.push({
                key: key,
                name: data.name,
                description: data.description,
                about: data.about,
                category: data.category
            });

        });
        setPosts(posts);
    }

     useEffect(() => {
        PostService.getAll().on('value', onDataChange);

        // posts.forEach (() => {
        //     // console.log(posts)
        //     if(postColor === 0) {
        //         $('.activityCard').addClass('hiking')
        //     } else if (postColor === 1) {
        //         $('.activityCard').addClass('backpacking')
        //     } else if (postColor === 2) {
        //        $('.activityCard').addClass('climbing')
        //     }
        // })

        return () => {
            PostService.getAll().off('value', onDataChange);
        }
    }, []) 

  for (var i = 0; i < posts.length; i++) {
      console.log(posts[i].category);
      if (posts[i].category === 'Hiking') {
        $('.activityCard').addClass('hiking');
      } else if (posts[i].category === 'Backpacking') {
        $('.activityCard').addClass('backpacking');
      } else if (posts[i].category === 'Climbing') {
        $('.activityCard').addClass('climbing');
      }
  }

  const grabPostId = () => {
    //   console.log(get().then((snapshot) => {
        // const data = 
    // }))
  }

    return (
        <Container fluid className='text-center' id='container' style={{maxWidth: '1200px'}}>
            <Row>
                <Header />
            </Row>
            <Row>
                <PostButton />
            </Row>
            {/* <SearchBar /> */}
            <Row>
                {/* <ButtonOne onClick={ClickButton}>Browse All</ButtonOne> */}
            </Row>
            <Row className='justify-content-center'>
                <ChooseActivity name='Hiking' />
                <ChooseActivity name='Biking' />
                <ChooseActivity name='Climbing' />
                <ChooseActivity name='Trail Running' />
                <ChooseActivity name='Rafting' />
                <ChooseActivity name='Backpacking' />
                <ChooseActivity name='Ice Climbing' />
                <ChooseActivity name='Bike Brewery Tours' />
                <ChooseActivity name='Paragliding' />
                <ChooseActivity name='Other' />
            </Row>

           <Row className='justify-content-center'>
           {posts.map((post) => (
               <div style={{width: '12rem', height: '12rem', border: '1px solid black', display: 'inline-block', margin: '1rem'}} id='color' className='activityCard' onClick={grabPostId}>
               <h3>{post.name}</h3>
               <h6>{post.description}</h6>
               <h6>{post.category}</h6>
               </div>
           ))}
           </Row>

      </Container>
    )
}

export default Home;
