import React, { useState, useEffect } from 'react';
import '../index.css';
import Header from '../components/header/Header';
import styled from 'styled-components';
import { PostButton } from '../components/buttons/Buttons';
import { ActivityCard, ChooseActivity } from '../components/card/card';
import { SearchBar } from '../components/input/Input';
import Container from 'react-bootstrap/Container';
// import Row from 'react-bootstrap/Row';
// import Col from 'react-bootstrap/Col';
import {db} from '../firebase';

const ButtonOne = styled.button`
    color: pink;
`

const Home = () => {
    // const [buttonState, setButtonState] = useState(false);
    // const [count, setCount] = useState(0);
    // const [count, setCount] = useState(0);
    const [post, setPost] = useState([]);

    //   useEffect(() => {
    //     setInterval(() => setDate(new Date()), 3000)
    //   })

    useEffect(() => {
        getPostInfo();
    }, [])

    const getPostInfo = async() => {
        const response = db.collection('posts');
        const data = await response.get();
        data.docs.forEach(post => {
            setPost([...post, post.data()])
        })
    }

    // const ClickButton = () => {
    //     // setButtonState(buttonState => !buttonState);
    //     // console.log(buttonState);
    //     setCount(new Date().toLocaleDateString());
    // };

    const getUserInfo = () => {
        // grab user info from API and add to cards
    }

    return (
        <Container fluid className='text-center' id='container'>
            <Header />
            <PostButton />
            {/* <SearchBar /> */}
            <div>
                {/* <ButtonOne onClick={ClickButton}>Browse All</ButtonOne> */}
            </div>

            <ChooseActivity name='Hiking' />
            <ChooseActivity name='Biking' />
            <ChooseActivity name='Climbing' />
            <ChooseActivity name='Trail Running' />
            <ChooseActivity name='Rafting' />
            <ChooseActivity name='Backpacking' />
            <ChooseActivity name='Ice Climbing' />
            <ChooseActivity name='Bike Brewery Tours' />
            <ChooseActivity name='Paragliding' />
            <ChooseActivity name='Other' />

            {/* Map over any exisiting posts */}
           { post && post.map(postItem => {
                return (
                    <div>
                        <h3>{post.name}</h3>
                        <h3>{post.description}</h3>
                        <h3>{post.about}</h3>
                    </div>
                )
            })
}
      </Container>
    )
}

export default Home;
