import React, { useState } from 'react';
import styled from 'styled-components';
import StarRatings from 'react-star-ratings';

const InputText = styled.input.attrs(props => ({
    type: "text",
    size: props.size || "1em",
  }))`
    border: 2px solid palevioletred;
    margin: ${props => props.size};
    padding: ${props => props.size};
  `;

  export const SearchBar = () => {
      return (
        <div>
            <InputText placeholder='Search an activity' 
            size='1rem' />
        </div>
      )
  }

  export const ActivityName = () => {
      const [name, setName] = useState('');

      const onChangeEvent = e => {
          setName(e.target.value);
      }

      return (
          <div>
              <h3>Activity Name:</h3>
            <InputText 
            placeholder='Enter name of activity' 
            size='1rem;'
            value={name}
            onChange={onChangeEvent}
             />
             <p></p>
          </div>
      )
  }

  export const ActivityDescription = () => {
      return (
          <div>
              <h3>Activity Description:</h3>
            <InputText placeholder='Enter description of activity - be specific! Distance, your desired pace, etc.' size ='1rem' />
          </div>
      )
  }

  export const StarRating = () => {
      const [rate, setRate] = useState();
      return (
          <>
          <h3>Skill Level:</h3>
            <StarRatings
            rating={rate}
            starRatedColor="yellow"
            starHoverColor="pink"
            changeRating={setRate}
            numberOfStars={5}
            name='rating'
            />
        </>
      )
  }

  export const UploadImage = () => {
      return (
          <>
            <h3>Images:</h3>
            <h6>Upload images of the activity, yourself, and whatever else might get a potential adventure partner interested in joining!</h6>
          </>
      )
  }

  export const UserDescription = () => {
      return (
          <>
          <h3>About You:</h3>
            <InputText placeholder="Please enter information about yourself - Where you're from, hobbies, how you adventure (fast, slow, like converstions, etc)" size='1rem' />
          </>
      )
  }
