import React, {useEffect} from 'react';
import PostService from '../components/services/PostService';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const Posting = () => {
    const [posts, setPosts] = useState([]);

    const onPageLoad = (items) => {
        let posts = [];

        items.forEach((item) => {
            let key = item.key;
            let data = item.val();
            posts.push({
                key: key,
                name: data.name,
                description: data.description,
                about: data.about,
                category: data.category
            })
        })
        setPosts(posts);
    }

    useEffect(() => {
        PostService.where('category', '==', 'Hiking')
    }, [])

    // const deletePost = () => {
    //     // allow user to delete post
    // }

    // const editPost = () => {
    //     // allow user to edit post
    // }

    return (
        <Container fluid className='text-center' style={{maxWidth: '1200px'}}>
            <Row>
                {posts.map((post))}
            </Row>
        </Container>
    )
} 

export default Posting;
