import React from 'react';
import styled from 'styled-components';

const InputText = styled.input.attrs(props => ({
    type: "text",
    size: props.size || "1em",
  }))`
    border: 2px solid palevioletred;
    margin: ${props => props.size};
    padding: ${props => props.size};
  `;

  export const SearchBar = () => {
      return (
        <div>
            <InputText placeholder='Search an activity' size='1rem' />
        </div>
      )
  }

  export const ActivityName = () => {
      return (
          <div>
            <InputText placeholder='Enter name of activity' size='1rem;' />
          </div>
      )
  }

  export const ActivityDescription = () => {
      return (
          <div>
            <InputText placeholder='Enter description of activity - be specific! Distance, your desired pace, etc.' size ='1rem' />
          </div>
      )
  }
